import { useState } from 'react';
import Img from 'react-image'
import React from 'react';
import Link from 'next/link';
import Router from 'next/router';
import NProgress from 'nprogress';
import { APP_NAME } from '../config';
import { signout, isAuth } from '../actions/auth';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink
} from 'reactstrap';
import '.././node_modules/nprogress/nprogress.css';
import Search from './blog/Search';
import "../static/css/styles.css";
import MetaTags from 'react-meta-tags'

Router.onRouteChangeStart = url => NProgress.start();
Router.onRouteChangeComplete = url => NProgress.done();
Router.onRouteChangeError = url => NProgress.done();

const Header = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  };

  <MetaTags name="viewport" content="width=device-width, initial-scale=1"></MetaTags>
  return (
    <React.Fragment>
      <Navbar id="header1" color="dark" light expand="md" >
      <img src="/AE3.png" alt="logo" width="75" height="60" />
        <Link href="/">
          <NavLink style={{ cursor: 'pointer' }} className="font-weight-bold" style={{fontFamily:"edo"}}>{APP_NAME}</NavLink>
        </Link>
        <NavbarToggler className = "hamburger" onClick={toggle} />
        <Collapse className="collapse"  isOpen={isOpen} navbar>
          <Nav className="ml-auto" navbar>
            <React.Fragment>
              <NavItem>
                <Link href="/blogs">
                  <NavLink style={{ cursor: 'pointer' }} id="header">Blogs</NavLink>
                </Link>
              </NavItem>
            </React.Fragment>

            {!isAuth() && (
              <React.Fragment>
                <NavItem>
                  <Link href="/signin">
                    <NavLink  style={{ cursor: 'pointer' }} id="header">Signin</NavLink>
                  </Link>
                </NavItem>
                <NavItem>
                  <Link href="/signup">
                    <NavLink style={{ cursor: 'pointer' }} id="header">Signup</NavLink>
                  </Link>
                </NavItem>
              </React.Fragment>
            )}

            {/* {isAuth() && isAuth().role === 0 && 
            (
              <NavItem>
                <Link href="/user">
                  <NavLink style={{ cursor: 'pointer', color: 'white', fontFamily: 'edo'}}>{`${isAuth().name}'s Dashboard`}</NavLink>
                </Link>
              </NavItem>
            )
            } */}

            {isAuth() && isAuth().role === 1 && (
              <NavItem>
                <Link href="/admin">
                  <NavLink style={{ cursor: 'pointer', color: 'white', fontFamily:' edo'}} id="header">{`${isAuth().name}'s Dashboard`}</NavLink>
                </Link>
              </NavItem>
            )}

            {isAuth() && (
              <NavItem>
                <NavLink style={{ cursor: 'pointer', color: 'white', fontFamily: 'edo'}} id="header" onClick={() => signout(() => Router.replace(`/signin`))}>
                  Signout
                </NavLink>
              </NavItem>
            )}
          </Nav>
        </Collapse>
      </Navbar>
      <Search />
    </React.Fragment>
  );
};

export default Header;
